import "./App.css";
//npm i socket.io-client (versión para react)
import io from "socket.io-client";
import Select from "./Select.jsx";
import { useState, useEffect } from "react";
import axios from "axios";
//Conexión para escuchar y enviar eventos
const socket = io("http://localhost:4000");

function App() {
  const [nickname, setNickname] = useState("");
  const [disabled, setDisabled] = useState(false);

  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [select, setSelect] = useState("");
  const [storedMessages, setStoredMessages] = useState([]);
  const [firstTime, setfirstTime] = useState(false);

  const url = "http://localhost:4000/api/";

  useEffect(() => {
    const receivedMessage = (message) => {
      //console.log(message)
      setMessages([message, ...messages]);
    };
    socket.on("message", receivedMessage);

    //Desuscribimos el estado del componente cuando ya no es necesario utilizarlo
    return () => {
      socket.off("message", receivedMessage);
    };
  }, [messages]);

  //Cargamos los mensajes guardados en la BD la primera vez
  if (!firstTime) {
    axios.get(url + "messages").then((res) => {
      setStoredMessages(res.data.messages);
    });
    setfirstTime(true);
  }

  const handlerSubmit = (e) => {
    //Evitamos recargar la página
    e.preventDefault();

    //Enviamos el mensaje sólo si se ha establecido un nickname
    if (nickname !== "") {
      //console.log(message)
      //Enviamos el mensaje al servidor
      socket.emit("message", message, nickname);

      //Nuestro mensaje
      const newMessage = {
        body: message,
        from: "Yo",
      };
      //Añadimos el mensaje y el resto de mensajes enviados
      setMessages([newMessage, ...messages]);
      //Limpiamos el mensaje
      setMessage("");

      //Petición http por POST para guardar el artículo:
      axios.post(url + "save", {
        message: message,
        from: nickname,
      });
    } else {
      alert("Para enviar mensajes debes establecer un nickname!!!");
    }
  };

  const nicknameSubmit = (e) => {
    e.preventDefault();
    setNickname(nickname);
    //console.log(nickname)
    setDisabled(true);
  };

  return (
    <div className="App">
      <div className="container mt-3">
        <div className="card shadow border-0">
          <div className="card-body">
            <h5 className="text-center mb-3">CHAT</h5>

            {/* nickname */}

            <form onSubmit={nicknameSubmit}>
              <div className="d-flex mb-3">
                <input
                  type="text"
                  className="form-control"
                  id="nickname"
                  placeholder="Nickname..."
                  disabled={disabled}
                  onChange={(e) => setNickname(e.target.value)}
                  value={nickname}
                  required
                />
                <button
                  className="btn btn-success mx-3"
                  type="submit"
                  id="btn-nickname"
                  disabled={disabled}
                >
                  Establecer
                </button>
              </div>
            </form>
            {/*select*/}
            <div className="container">
              <select name="select">
                <option value="ususario">ususario</option>
                <option value="compositor">compositor</option>
                <option value="escritor">escritor</option>
              </select>
            </div>

            {/* chat form */}

            <form onSubmit={handlerSubmit}>
              <div className="d-flex">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Mensaje..."
                  onChange={(e) => setMessage(e.target.value)}
                  value={message}
                />
                <button className="btn btn-success mx-3" type="submit">
                  Enviar
                </button>
              </div>
            </form>
          </div>
        </div>

        {/* chat messages */}

        <div className="card mt-3 mb-3 shadow border-0" id="content-chat">
          <div className="card-body">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  message.from === "Yo"
                    ? "justify-content-end"
                    : "justify-content-start"
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    message.from === "Yo"
                      ? "bg-success bg-opacity-25"
                      : "bg-light"
                  }`}
                >
                  <div className="card-body">
                    <small className="">
                      {message.from}: {message.body}
                    </small>
                  </div>
                </div>
              </div>
            ))}

            {/* chat stored messages */}
            <small className="text-center text-muted">
              ... Mensajes guardados ...
            </small>
            {storedMessages.map((message, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  message.from === nickname
                    ? "justify-content-end"
                    : "justify-content-start"
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    message.from === nickname
                      ? "bg-success bg-opacity-25"
                      : "bg-light"
                  }`}
                >
                  <div className="card-body">
                    <small className="text-muted">
                      {message.from}: {message.message}
                    </small>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
